import React, { useState, useEffect } from "react";
import axios from "axios";
import Card from "./components/card";

function Projetos() {
  const [repositories, setRepositories] = useState([]);

  useEffect(() => {
    const fetchRepositories = async () => {
      try {
        // Substitua 'seu-usuario' pelo seu nome de usuário do GitHub
        const response = await axios.get(
          "https://api.github.com/users/talitacastrofreitas/repos"
        );

        const repositoriesWithImages = await Promise.all(
          response.data.map(async (repo) => {
            // Obter detalhes específicos do repositório, incluindo a imagem de pré-visualização
            const repoDetails = await axios.get(`https://api.github.com/repos/${repo.full_name}`);
            
            return {
              ...repo,
              image_url: repoDetails.data.owner.avatar_url, // Aqui estamos usando a imagem do proprietário do repositório como exemplo
            };
          })
        );

        setRepositories(repositoriesWithImages);
      } catch (error) {
        console.error("Erro ao obter a lista de repositórios", error);
      }
    };

    fetchRepositories();
  }, []);

  return (
    <div>
      <h1>Lista de Repositórios</h1>
      {repositories.map((repo) => (
        <Card
          key={repo.id}
          name={repo.name}
          description={repo.description}
          html_url={repo.html_url}
          image_url={repo.image_url}
        />
      ))}
    </div>
  );
}

export default Projetos;
