import { useEffect, useState } from 'react';
import Card from './components/card';
import './projetos.css';
import Header from './components/header';

function Projetos() {
  const [repositories, setRepositories] = useState([]);

  useEffect(() => {
    const buscarRepositorios = async () => {
      try {
        const resposta = await fetch('https://api.github.com/users/talitacastrofreitas/repos');
        const dados = await resposta.json();

        const repositoriosComImagens = await Promise.all(
          dados.map(async (repo) => {
            const readmeResponse = await fetch(`https://raw.githubusercontent.com/${repo.full_name}/main/README.md`);
            const readmeText = await readmeResponse.text();

            const parser = new DOMParser();
            const doc = parser.parseFromString(readmeText, 'text/html');

            const imgElement = doc.querySelector('img');
            const imageUrl = imgElement ? imgElement.src : null;

            return {
              ...repo,
              imageUrl,
            };
          })
        );

        setRepositories(repositoriosComImagens);
      } catch (error) {
        console.error('Erro ao buscar repositórios:', error);
      }
    };

    buscarRepositorios();
  }, []);

  return (
    <div>
      <Header />
      <div className="container mt-4">
        <h2 className="text-white mb-4">PROJETOS</h2>
        <div className="row">
          {repositories.length > 0 ? (
            repositories.map((repo) => (
              <div key={repo.id} className="col-lg-3 mb-3">
                <Card
                  social_preview={repo.imageUrl || 'caminho_da_imagem_padrao.jpg'}
                  name={repo.name}
                  description={repo.description}
                  html_url={repo.html_url}
                />
              </div>
            ))
          ) : (
            <p className="text-white">Carregando repositórios...</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Projetos;
