import { useEffect, useState } from 'react';
import Card from './components/card';
import './projetos.css';
import Header from './components/header';
import axios from 'axios';

function Projetos() {
  const [repositories, setRepositories] = useState([]);

  useEffect(() => {
    const fetchRepositories = async () => {
      try {
        const response = await axios.get('https://api.github.com/users/talitacastrofreitas/repos');
        const data = response.data;

        const repositoriesWithImages = await Promise.all(
          data.map(async (repo) => {
            const proxyUrl = 'http://localhost:3000/proxy?url=';  // Adicione esta linha
            const readmeResponse = await axios.get(`${proxyUrl}https://raw.githubusercontent.com/${repo.full_name}/master/README.md`);
            const readmeContent = readmeResponse.data;

            // Regex para encontrar URLs de imagens no Markdown
            const imageRegex = /!\[.*?\]\((.*?)\)/g;
            const matches = [...readmeContent.matchAll(imageRegex)];

            if (matches.length > 0) {
              const imageUrl = matches[0][1];

              // Faz a requisição para a URL da imagem
              const imageResponse = await axios.get(imageUrl, { responseType: 'arraybuffer' });
              const imageBlob = new Blob([imageResponse.data], { type: imageResponse.headers['content-type'] });

              // Cria uma URL de objeto (blob) para a imagem
              const imageUrlBlob = URL.createObjectURL(imageBlob);

              return { ...repo, image: imageUrlBlob };
            } else {
              return repo;
            }
          })
        );

        setRepositories(repositoriesWithImages);
      } catch (error) {
        console.error('Erro ao obter a lista de repositórios', error);
      }
    };

    fetchRepositories();
  }, []);

  return (
    <div>
      <Header />
      <div className="container mt-4">
        <h2 className="text-white mb-4">PROJETOS</h2>
        <div className="row">
          {repositories.length > 0 ? (
            repositories.map((repo) => (
              <div key={repo.id} className="col-lg-3 mb-3">
                <Card
                  social_preview={repo.social_preview}
                  name={repo.name}
                  description={repo.description}
                  html_url={repo.html_url}
                  image={repo.image}
                />
              </div>
            ))
          ) : (
            <p className="text-white">Carregando repositórios...</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Projetos;
