import { useEffect, useState } from 'react';
import Card from './components/card';
import './projetos.css';
import Header from './components/header';
import { Remarkable } from 'remarkable';

function Projetos() {
  const [repositories, setRepositories] = useState([]);

  useEffect(() => {
    const buscarRepositorios = async () => {
      try {
        const resposta = await fetch('https://api.github.com/users/talitacastrofreitas/repos');
        const dados = await resposta.json();

        const mdParser = new Remarkable();

        const repositoriosComImagens = await Promise.all(
          dados.map(async (repo) => {
            // Buscar o conteúdo do README.md
            const readmeResponse = await fetch(`https://raw.githubusercontent.com/talitacastrofreitas/${repo.full_name}/master/README.md`);
            const readmeContent = await readmeResponse.text();

            // Analisar o conteúdo do README.md para encontrar imagens
            const tokens = mdParser.parse(readmeContent, {});

            let imageUrl = null;

            tokens.forEach((token) => {
              if (token.type === 'image') {
                imageUrl = token.href;
              }
            });

            return {
              ...repo,
              image_url: imageUrl,
            };
          })
        );

        setRepositories(repositoriosComImagens);
      } catch (error) {
        console.error('Erro ao obter a lista de repositórios', error);
      }
    };

    buscarRepositorios();
  }, []);

  return (
    <div>
      <Header />
      <div className="container mt-4">
        <h2 className="text-white mb-4">PROJETOS</h2>
        <div className="row">
          {repositories.length > 0 ? (
            repositories.map((repo) => (
              <div key={repo.id} className="col-lg-3 mb-3">
                <Card social_preview={repo.social_preview} name={repo.name} description={repo.description} html_url={repo.html_url} image_url={repo.image_url} />
              </div>
            ))
          ) : (
            <p className="text-white">Carregando repositórios...</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Projetos;
