import { useEffect, useState } from 'react';
import Card from './components/card';
import './projetos.css';
import Header from './components/header';

function Projetos() {
  const [repositories, setRepositories] = useState([]);

// ...

useEffect(() => {
  const buscarRepositorios = async () => {
    try {
      const resposta = await fetch('https://api.github.com/users/talitacastrofreitas/repos');
      const dados = await resposta.json();

      const repositoriosComImagens = await Promise.all(
        dados.map(async (repo) => {
          try {
            // Use a API pública cors-anywhere para contornar o bloqueio CORS
            const corsProxy = 'https://cors-anywhere.herokuapp.com/';
            const readmeResponse = await fetch(`${corsProxy}https://raw.githubusercontent.com/${repo.full_name}/main/README.md`);
            const readmeBlob = await readmeResponse.blob();

            // Criar uma URL para o Blob
            const readmeUrl = URL.createObjectURL(readmeBlob);

            // Lógica para extrair a URL da imagem do conteúdo do README.md
            const matches = readmeUrl.match(/!\[.*?\]\((.*?)\)/);

            return {
              ...repo,
              image_url: matches ? matches[1] : null,
            };
          } catch (error) {
            console.error(`Erro ao buscar README.md do repositório ${repo.full_name}`, error);
            return {
              ...repo,
              image_url: null,
            };
          }
        })
      );

      setRepositories(repositoriosComImagens);
    } catch (error) {
      console.error('Erro ao buscar repositórios', error);
    }
  };

  buscarRepositorios();
}, []);

// ...



  return (
    <div>
      <Header />
      <div className="container mt-4">
        <h2 className="text-white mb-4">PROJETOS</h2>
        <div className="row">
          {repositories.length > 0 ? (
            repositories.map((repo) => (
              <div key={repo.id} className="col-lg-3 mb-3">
                <Card
                  social_preview={repo.social_preview}
                  name={repo.name}
                  description={repo.description}
                  html_url={repo.html_url}
                  image_url={repo.image_url}
                />
              </div>
            ))
          ) : (
            <p className="text-white">Carregando repositórios...</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Projetos;
