import React, { useState, useEffect } from "react";
import axios from "axios";
import Card from "./components/card";
import Header from './components/header'
function Projetos() {
  const [repositories, setRepositories] = useState([]);

  useEffect(() => {
    const fetchRepositories = async () => {
      try {
        // Substitua 'seu-usuario' pelo seu nome de usuário do GitHub
        const response = await axios.get(
          "https://api.github.com/users/talitacastrofreitas/repos"
        );

        const repositoriesWithImages = await Promise.all(
          response.data.map(async (repo) => {
            // Obter o conteúdo do README.md
            const readmeResponse = await axios.get(
              `https://raw.githubusercontent.com/${repo.full_name}/master/README.md`
            );

            // Extrair a URL da imagem de pré-visualização do README.md
            const imageUrl = extractImageUrlFromReadme(readmeResponse.data);

            return {
              ...repo,
              image_url: imageUrl,
            };
          })
        );

        setRepositories(repositoriesWithImages);
      } catch (error) {
        console.error("Erro ao obter a lista de repositórios", error);
      }
    };

    fetchRepositories();
  }, []);

  // Função para extrair a URL da imagem do README.md
  const extractImageUrlFromReadme = (readmeContent) => {
    // Lógica para extrair a URL da imagem do conteúdo do README.md
    // Implemente de acordo com o formato específico do seu README.md
    // Por exemplo, você pode usar expressões regulares ou outras técnicas de manipulação de strings.

    // Exemplo de lógica simples: procurar por uma tag de imagem markdown
    const match = readmeContent.match(/!\[.*\]\((.*?)\)/);

    // Retorna a URL da imagem se encontrada, ou uma URL padrão de imagem de fallback
    return match ? match[1] : "URL_DE_FALLBACK";
  };

  return (
    <div>
      <Headers/>
      <h1 className="text-white">PROJETOS</h1>
      {repositories.map((repo) => (
        <Card
          key={repo.id}
          name={repo.name}
          description={repo.description}
          html_url={repo.html_url}
          image_url={repo.image_url}
        />
      ))}
    </div>
  );
}

export default Projetos;
